public class Test{    public static void main(String[] args) {        long startTime = System.currentTimeMillis();        long duration = 3000;         while (System.currentTimeMillis() - startTime < duration) {                        calculatePrimeNumbers();        }        System.out.println('끝!');    }    private static void calculatePrimeNumbers() {        int limit = 10000;        for (int number = 2; ; number++) {            boolean isPrime = true;            for (int divisor = 2; divisor < number; divisor++) {                if (number % divisor == 0) {                    isPrime = false;                    break;                }            }            if (isPrime) {                limit--;                if (limit == 0) {                    break;                }            }        }    }}